			--select *From #tmpNotificationRequest

---insert  Notification  Request----
create table #NotificationRequestId([ID] [int] NOT NULL,)


		INSERT INTO [dbo].[NotificationRequest] ([EventNotificationId] ,[NotifcationType] ,[Title] ,[BodyContent] ,[RecipientTo] ,[RecipientCC] ,[RecipientBCC] ,LoginId,[MessageId],[DeviceToken],[DeviceType],[PushNotificationType],[Sound],[Badge],[NotificationRequestGuid],[IsSent] ,[IsSentDatetime] ,[IsActive] ,[CreatedBy] ,[CreatedDate])
		OUTPUT INSERTED.NotificationRequestId
