		select   #tmpNotificationRequest.EventNotificationId,  #tmpNotificationRequest.NotificationType, #tmpNotificationRequest.Title, #tmpNotificationRequest.BodyContent, #tmpNotificationRequest.RecipientTo, #tmpNotificationRequest.RecipientCC, #tmpNotificationRequest.RecipientBCC,#tmpNotificationRequest.LoginId,#tmpNotificationRequest.MessageId,#tmpNotificationRequest.DeviceToken,#tmpNotificationRequest.DeviceType,#tmpNotificationRequest.PushNotificationType,#tmpNotificationRequest.Sound,#tmpNotificationRequest.Badge,#tmpNotificationRequest.NotificationRequestGuid,  #tmpNotificationRequest.IsSent, #tmpNotificationRequest.IsSentDatetime,1,1,GETDATE() from #tmpNotificationRequest



--select *From NotificationRequest  where NotificationRequestId  in (select ID From   #NotificationRequestId)



		--select #tmpNotificationRequest.*  , nr.NotificationRequestId From    #tmpNotificationRequest join NotificationRequest  nr  on nr.NotificationRequestGuid =#tmpNotificationRequest.NotificationRequestGuid
		--where   isnull(nr.NotificationRequestGuid,'')='' ;


-----return
		
;WITH XMLNAMESPACES('http://james.newtonking.com/projects/json' AS json) 
