Create PROCEDURE [dbo].[SSP_AllTruckSizeListByVehicleId] --'<Json><ServicesAction>GetAllTruckSizeListByVehicleId</ServicesAction><VehicleTypeId>61</VehicleTypeId></Json>'@xmlDoc XMLASBEGINDECLARE @intPointer INT;declare @vehicleType bigint=0declare @ItemValue  nvarchar(250);EXEC sp_xml_preparedocument @intpointer OUTPUT,@xmlDocSELECT @vehicleType = tmp.[VehicleTypeId]	 FROM OPENXML(@intpointer,'Json',2)			WITH			(			[VehicleTypeId] bigint           			)tmp ;WITH XMLNAMESPACES('http://james.newtonking.com/projects/json' AS json) SELECT CAST((SELECT 'true' AS [@json:Array] , [TruckSizeId]				  ,[TruckSize]				  ,[TruckCapacityPalettes]				  ,[TruckCapacityWeight]				  ,[IsActive]				   FROM [TruckSize] WHERE IsActive = 1 and ISNULL([TruckCapacityWeight],0) != 0 and VehicleType=@vehicleType	FOR XML path('TruckSizeList'),ELEMENTS,ROOT('Json')) AS XML)END